function populateDeviceTypes() {
    var deviceTypes = datasources.devadm_device_types.latestData();
    var innerContent = `<div class="list-group" id="showDeviceTypes"></div>`;
    $('#div-for-device-type-list').empty().append(innerContent);
    $('#showDeviceTypes').empty();
    for (var i in deviceTypes) {
        var typeName = deviceTypes[i].data.type;
        var childButton = createDeviceTypeButton(typeName)
        $('#showDeviceTypes').append(childButton);
    }
}

function createDeviceTypeButton(typeName) {
    return `<div class="row">

        <button class="list-group-item form-control" style="margin-left: 15px;margin-right: 15px; type="button" 
            onclick="updateSelectedDeviceType('` + typeName + `')">` + typeName + 
            `
        </button>
    </div>`;
}

