    datasources.devadm_device_types.refresh();
    populateDeviceTypes();
}

window.addNewDeviceType = function(){
    var childInputField = `<input class="list-group-item" autofocus placeholder="New Type">`
    $('#showDeviceTypes').append(childInputField);
    var saveButton = `<button class="btn btn-success" onclick="saveNewDeviceType()">Save</button>`
    $('#save-button').empty().append(saveButton);
}

window.saveNewDeviceType = function(){
    $('#save-button').empty();
    var inputFields = $('#div-for-device-type-list').find('input');
    newDeviceTypes = []
    for(var i=0; i< inputFields.length;i++){
        newDeviceTypes.push(inputFields[i].value);
    }
    datasources.devadmTypeUtil.sendData({
        path:"create_device_type",
        deviceType:newDeviceTypes[0]
    }).then(function(data){
        datasources.devadm_device_types.refresh();
        populateDeviceTypes();
    });

}

