//JavaScript
var messageHistoryForSelectedDevice;
var currSelectedMessageId = null;


$("#messageTypeSelect").change(function(){
    showMessageHistory();
});



showMessageHistory();



function showMessageHistory() {
    var currDevice = datasources.selectedDevice.latestData();
    currSelectedMessageId = null;
    $('#headingTag').html("Device Message History for " + currDevice.name);
    var messageType = $("#messageTypeSelect").val();
    var statusTopic = ["devices", currDevice.name, messageType].join('/');

    datasources.devadmMessageUtil.sendData({
        messageTopic: statusTopic,
        path:"get_topic_history"
    }).then(function (data) {
        var innerContent = `<div><div class="list-group" id="showDeviceHistory"></div></div>`;
        $('#div-for-message-list').empty().append(innerContent);
        messageHistoryForSelectedDevice = data.results.result;
        var userIdToEmail = {};
        datasources.devadmGetAllUsers.sendData({}).then(function (data) {
            var users = data.results.Data;
            for (var i in users) {
                userIdToEmail[users[i]["user_id"]] = users[i]["email"];
            }
            $('#showDeviceHistory').empty();
            for (var i in messageHistoryForSelectedDevice) {
                var childButtonData = getMessageButtonData(messageHistoryForSelectedDevice[i], userIdToEmail);
                var childButton = createMessageButton(messageHistoryForSelectedDevice[i], i, childButtonData)
                $('#showDeviceHistory').prepend(childButton);
            }

        });
    });
}

function createMessageButton(messageForDevice, i, childButtonData) {
    return `<button class="list-group-item waves-effect" type="button" onclick="showCompleteMessage(` + i + `)">` + childButtonData + `</button>`;
}

function getMessageButtonData(messageForDevice, userIdToEmail) {
    var dateFromEpochInSeconds = parseInt(messageForDevice["send-date"]);
    var d = new Date(0);
    d.setUTCSeconds(dateFromEpochInSeconds);
    var timeStamp = d.toString().split("GMT")[0];
    var deviceUserId = messageForDevice["user-id"];
    var UserEmail = userIdToEmail[deviceUserId];
    var childButtonData = UserEmail + " " + timeStamp;
    return childButtonData;
}


function getUserIdToEmailMap() {
    return userIdToEmail;
}

window.RefreshCurrentTab = function () {
    showMessageHistory();
}

window.showCompleteMessage = function (id) {
    document.getElementById('messageDiv').setAttribute('class', 'block');
    $("#messageDiv").show();
    var mesgObj = JSON.parse(messageHistoryForSelectedDevice[id]["message"]);
    var messageContent = JSON.stringify(mesgObj, undefined, 4);
    $("textarea#showMessageDetails").val(messageContent);
    document.getElementById('deleteMessageButton').disabled = false;
    currSelectedMessageId = id;
}

window.deleteThisMessage = function () {
    var deviceName = datasources.selectedDevice.latestData().name;
    var messageType = $("#messageTypeSelect").val();
    var topic = ["devices", deviceName, messageType].join('/');
    var messageTimeStamp = messageHistoryForSelectedDevice[currSelectedMessageId]["send-date"];
    datasources.devadmMessageUtil.sendData({
        "path":"delete_message",
        "messageTopic": topic,
        "timeStampToDelete": messageTimeStamp
    }).then(function (err, r) {
        if (err) {
            console.log(err);
        }
        else {
            toaster.show("Success");
            //console.log(JSON.stringify(r));
        }
    });
    document.getElementById('showMessageDetails').innerHTML = "";
    document.getElementById('deleteMessageButton').disabled = true;
    setTimeout(function(){ showMessageHistory() }, 500);
}

function removeElement(elementId) {

    try {
        var element = document.getElementById(elementId);
        element.parentNode.removeChild(element);
    }
    catch (e) {

    }
}

window.resendThisMessage = function () {
    var payload = JSON.parse(document.getElementById("showMessageDetails").value);
    var currDevice = datasources.selectedDevice.latestData();
    var messageType = $("#messageTypeSelect").val();
    var statusTopic = ["devices", currDevice.name, messageType].join('/');
    datasources.devadmMessageUtil.sendData({
        "path":"publish_message",
        "messageTopic": statusTopic,
        "payload": payload
    }).then(setTimeout(function(){ showMessageHistory() }, 500));


}

