function populateDeviceSchema() {
    var deviceColumns = Object.keys(datasources.selectedDevice);
    var innerContent = `<div><div class="list-group" id="showDeviceColumns"></div></div>`;
    $('#div-for-attribute-list').empty().append(innerContent);
    $('#showDeviceColumns').empty();

    var selectedDeviceType = datasources.SelectedDeviceType.latestData();
    if (selectedDeviceType == null) {
        var deviceTypeNameField = `<label class="col-sm-3 row">Device Type Name:</label><input type="text" class="form-control col-md-5" value="New Device Type">`
        $('#device-type-form').append(deviceTypeNameField);
    }
    else {
        datasources.devadmTypeUtil.sendData({
            "path":"get_device_type",
            "deviceType": selectedDeviceType
        }).then(function (data) {
            if (data.success) {
                var schemaJSONObj = JSON.parse(data.results.result[0].schema);
                $('#showDeviceColumns').empty();
                for (var i in schemaJSONObj) {
                    if (schemaJSONObj.hasOwnProperty(i)) {
                        var childButton = createDeviceSchemaRows(i, schemaJSONObj[i], "checked", "disabled");
                        $('#showDeviceColumns').append(childButton);
                    }
                }
            }

        });

    }

}

function createDeviceSchemaRows(columnName, columnType, checked, disabled) {
    var ret= `<div class="list-group-item">
    <div class="input-group pl-1 mb-1 input-group-addon">
  <div class="input-group-prepend">
    <div class="input-group-text">
      <input class="schema_input" type="checkbox" `+ checked + ` aria-label="Checkbox for following text input">
    </div>
  </div>
    <input type="text" ` + disabled + ` class="form-control col-md-5 schema_input" value="` + columnName + `" aria-label="...">`;
    if (disabled){
        ret = ret + `<input type="text" ` + disabled + ` class="form-control col-md-5 schema_input" value="` + columnType + `" aria-label="...">`;
    }else {
        ret = ret+ `<select type="text"  ` + disabled + ` class="form-control col-md-5 schema_input" value="` + columnType + `" aria-label="...">
        <option value="string">string</option>
        <option value="int">int</option>
        <option value="bigint">bigint</option>
        <option value="float">float</option>
        <option value="double">double</option>
        <option value="blob">blob</option>
        <option value="uuid">uuid</option>
        <option value="timestamp">timestamp</option>
        <option value="boolean">boolean</option>
    </select>`;
    }
    ret = ret + `</div>
    </div>`;

;

    return ret;
}

window.AddAtrributeToSchema = function () {
    var childButton = createDeviceSchemaRows("", "", "checked", "");
    $('#showDeviceColumns').append(childButton);
    $('#UpdateDeviceTypeSchemaDiv').removeClass('d-block').addClass('d-none');
}

window.UpdateDeviceTypeSchema = function () {
    var deviceSchemaList = $('#div-for-attribute-list').find('.schema_input');
    var schema = {};
    for (var i = 0; i < deviceSchemaList.length; i += 3) {
        if (deviceSchemaList[i].checked) {
            var nameVal = deviceSchemaList[i + 1].value;
            var typetag = deviceSchemaList[i + 2];
            var typeVal = "";
            if (typetag.nodeName =="SELECT"){
                typeVal = typetag.options[typetag.selectedIndex].value
            }else {
                typeVal = deviceSchemaList[i + 2].value
            }
            schema[nameVal] = typeVal;
        }
    }
    var selectedDeviceType = datasources.SelectedDeviceType.latestData();
    datasources.devadmTypeUtil.sendData({
        "path":"update_device_type",
        "deviceType": selectedDeviceType,
        "schema": schema
    }).then(function (data) {
        if (data.success) {
            populateDeviceSchema()
        }
        else {
            populateDeviceSchema()
        }
    });
}

